    [module.set_array email headers.new="Cc: {module.email.cc.email_id}" not_empty='{module.email.cc.email_id}' /]
    [module.set_array email headers.new="Bcc: {module.email.bcc.email_id}" not_empty='{module.email.bcc.email_id}' /]

