//****Adds attachment for the application****// [templates.add add_attachment] //*****Check if object_id is present or not and object type is lapp or not*****// [if.empty template.object_id][/if.empty] [if.or cond={template.object_type} not_equal="lead"] [template.return template.email/] [/if.or] //***define lapp id on modular level****// [module.set lead_id={template.object_id}/] //****Check if any attachment is present or not, if present than attach file path to array****// [templates.attach_file_path email={template.email} set=template.email/] //*****Return email array****// [template.return template.email/] [/templates.add]
//****Finds attachment****// [templates.add attach_file_path] //*****if attachment count is zero, then return****// [if.equal lhs={template.email.attachments.file.count} rhs="0"] [template.return template.email/] [/if.equal] //*****loop through all the attachments****// [loop.@file template.email.attachments.file] [templates.fetch_path file_name={@file.item} set=template.file_path/] [php.wp_get_attachment_caption p1="{@file.item}" set="template.filename" /] [template.set_array email.attachments.file.{@file.counter} name="{template.filename}" path={template.file_path}/] [/loop.@file] //*****Return new array formed with file path******// [template.return template.email/] [/templates.add]
[templates.add attach_file_path_wpmail] //*****if attachment count is zero, then return****// [if.equal lhs={template.email.attachments.file.count} rhs="0"] [template.return template.email/] [/if.equal] //*****loop through all the attachments****// [loop.@file template.email.attachments.file] [php.get_attached_file p1="{@file.item}" set=template.file_path /] [template.set email.attachments.file.new={template.file_path}/] [/loop.@file]
//*****loop through all the attachments paths****// [loop.@file template.email.attachments.file_path] [aw2.get @file.item set=template.file_path /] [template.set email.attachments.file.new={template.file_path} /] [/loop.@file] [template.set email.attachments="{template.email.attachments.file}"/] //*****Return new array formed with file path******// [template.return template.email/] [/templates.add]
//****fetch file path stored against the lapp_id****// [templates.add fetch_path] [ignore.debug] //***root directory**// [php.dirname p1="{server_variables.DOCUMENT_ROOT}" p2="1" set=template.temp_path/] //***setting up the file upload path***// [template.set uploadpath="{template.temp_path}/uploads/{module.lead_id}" /] //****get the file path***// [php.glob p1="{template.uploadpath}/{template.file_name}" set=template.file_path /] //***updating file path***// [template.file_path.0 set=template.file_path/] [/ignore.debug]
[php.get_attached_file p1="{template.file_name}" set=template.file_path /] //***return the file path***// [template.return template.file_path/] [/templates.add]

