//** Update Meta data for one object_id **// [templates.add update_object_meta] [aw2.get current_user.user_email set=template.email/] [multi.update] start transaction;
select @object_id:="[template.get object_id /]" from dual; select @coll_id:="[template.get coll_id /]" from dual; [loop.@meta template.meta] select @cnt:=count(1) from [template.get meta_table /] where object_id=@object_id and coll_id=@coll_id and meta_key="[aw2.get @meta.item.meta_key/]"; insert into [template.get meta_table /](object_id,coll_id,coll_type,meta_key,meta_value,updated_by) select @object_id,@coll_id,"[aw2.get @meta.item.coll_type/]","[aw2.get @meta.item.meta_key/]","[aw2.get @meta.item.meta_value m.esc_sql=yes /]","[template.email /]" from dual where @cnt=0; update [template.get meta_table /] set coll_type="[aw2.get @meta.item.coll_type/]",meta_value="[aw2.get @meta.item.meta_value m.esc_sql=yes /]",updated_by="[template.email /]",stamp=current_timestamp where object_id=@object_id and coll_id=@coll_id and meta_key="[aw2.get @meta.item.meta_key/]" and @cnt=1; [/loop.@meta]
commit; [/multi.update] [/templates.add]
//** select object meta for one object_id **// [templates.add select_object_meta]
[template.set query empty="{template.meta_keys}"] select * from [template.get meta_table /] where coll_id="[template.get coll_id /]" and object_id="[template.get object_id /]"; [/template.set]
[template.get meta_keys.comma set=template.meta_keys not_empty="{template.meta_keys}"/]
[template.set query not_empty="{template.meta_keys}"] select * from [template.get meta_table /] where coll_id="[template.get coll_id /]" and object_id="[template.get object_id /]" and meta_key in ([template.get meta_keys.quote_comma/]); [/template.set]
[query.get_results set=template.rs] [template.get query/] [/query.get_results]
[template.set rs_arr/] [loop.@rs template.rs] [aw2.get @rs.item.meta_value set=template.rs_arr.{@rs.item.meta_key} /] [/loop.@rs]
[template.return template.rs_arr/] [/templates.add]
//** Insert One collection for One Object **// [templates.add insert_coll_meta] [aw2.get current_user.user_email set=template.email/] [multi.update] start transaction; [loop.@meta template.meta] insert into [template.get meta_table /](object_id,coll_id,coll_type,meta_key,meta_value,updated_by) VALUES ("[template.get object_id /]","[template.get coll_id/]","[template.get coll_type/]","[aw2.get @meta.item.meta_key/]","[aw2.get @meta.item.meta_value m.esc_sql=yes /]","[template.email/]"); [/loop.@meta] commit; [/multi.update]
[/templates.add]
//** Update One collection for One Object **// [templates.add update_coll_meta] [aw2.get current_user.user_email set=template.email/] [multi.update] start transaction; select @object_id:='[template.get object_id /]' from dual;
[loop.@meta template.meta] select @cnt:=count(1) from [template.get meta_table /] where object_id=@object_id and coll_id="[template.get coll_id /]" and coll_type="[template.get coll_type/]" and meta_key="[aw2.get @meta.item.meta_key/]";
insert into [template.get meta_table /] (object_id,coll_id,coll_type,meta_key,meta_value,updated_by)
select @object_id,"[template.get coll_id /]","[template.get coll_type/]","[aw2.get @meta.item.meta_key/]","[aw2.get @meta.item.meta_value m.esc_sql=yes /]","[template.email/]" from dual where @cnt=0;
update [template.get meta_table /] set meta_value="[aw2.get @meta.item.meta_value m.esc_sql=yes /]",updated_by="[template.email/]",stamp=current_timestamp where object_id=@object_id and coll_id="[template.get coll_id /]" and coll_type="[template.get coll_type/]" and meta_key="[aw2.get @meta.item.meta_key/]" and @cnt=1; [/loop.@meta]
commit; [/multi.update]
[/templates.add]
//** select collection meta for one collection **// [templates.add select_collection_meta] [template.set query empty="{template.meta_keys}"] select * from [template.meta_table/] where coll_id="[template.coll_id/]" and coll_type="[template.coll_type/]" and object_id='[template.object_id /]'; [/template.set]
[template.get meta_keys.comma set=template.meta_keys not_empty="{template.meta_keys}"/]
[template.set query not_empty="{template.meta_keys}"] select * from [template.meta_table/] where coll_id="[template.coll_id/]" and coll_type="[template.coll_type/]" and object_id="[template.object_id /]" and meta_key in ([template.meta_keys.quote_comma/]); [/template.set]
[query.get_results set=template.rs] [template.query/] [/query.get_results] [template.return template.rs/] [/templates.add]
//** select meta for one object_id **// [templates.add select_meta] [template.set query empty="{template.meta_keys}"] select * from [template.meta_table /] where object_id="[template.object_id /]"; [/template.set]
[template.get meta_keys.comma set=template.meta_keys not_empty="{template.meta_keys}"/]
[template.set query not_empty="{template.meta_keys}"] select * from [template.meta_table /] where object_id='[template.object_id /]' and meta_key in ([template.meta_keys.quote_comma/]); [/template.set]
[query.get_results set=template.rs][template.query/][/query.get_results]
[template.return template.rs/] [/templates.add]
//****Checke customer present with same email or not*****// [templates.add get_email] [query.get_results set=template.rs] SELECT * FROM [template.meta_table default=customer_meta/] WHERE meta_key="personal_email" and meta_value="[template.email_id /]" [/query.get_results] [template.return template.rs/] [/templates.add]

//************ Build the Args *************************//
[module.set attr] [loop.@ctr module.attr][aw2.get @ctr.key/]="[aw2.get @ctr.item/]" $spacer [/loop.@ctr] [/module.set] [aw2.get function.str_replace p1="$spacer" p2=" " p3="{module.attr}" set="module.attr_string"/]
<input id="[module.get id /]" name="[module.get id /]" type="hidden" class="form-control input-md [module.get class /]" value='[module.get value /]' honeypot="" [module.get attr_string/] />

[templates.add get_data] //***Posts Builder Part Start***// [query.posts_builder part=start] { [if.not_empty template.args.post_type] "post_type": "[aw2.get template.args.post_type /]", [/if.not_empty] [if.else] "post_type": [aw2.get template.args.post_arr /], [/if.else] "orderby": "[aw2.get template.args.orderby default="menu_order"/]", "order": "[aw2.get template.args.order default="ASC"/]", [if.not_equal lhs="{template.args.s}" rhs=""] "s": "[aw2.get template.args.s /]", [/if.not_equal] [if.not_empty template.args.tag__in] "tag__in": [ [aw2.get template.args.tag__in /] ], [/if.not_empty] [if.not_equal lhs="{template.args.post__in}" rhs=""] "post__in": [ [aw2.get template.args.post__in /] ], [/if.not_equal] [if.not_equal lhs="{template.args.post__not_in}" rhs=""] "post__not_in": [ [aw2.get template.args.post__not_in /] ], [/if.not_equal] [if.not_empty template.args.post_parent] "post_parent": "[aw2.get template.args.post_parent /]", [/if.not_empty] "posts_per_page": [aw2.get template.args.posts_per_page default= -1 /], "paged": "[aw2.get template.args.paged default=1 /]" } [/query.posts_builder]
//***Posts Builder Part Meta Query***// [loop.@meta_val template.args.meta not_empty="{template.args.meta}"] [query.posts_builder part=meta_query] { "relation":"or", [loop.@meta_item @meta_val.item.comma not_empty="{@meta_val.item}"] "[aw2.get @meta_item.key /]":{ "key": "[aw2.get @meta_val.key /]", "value": "\"[aw2.get @meta_item.item]\"", "compare": "LIKE" } [if.true @meta_item.between],[/if.true] [/loop.@meta_item] } [/query.posts_builder] [/loop.@meta_val]
//***Posts Builder Part Meta Query for single meta***// [loop.@meta_val template.args.single_meta not_empty='{template.args.single_meta}'] [query.posts_builder part=meta_query] { "key": "[aw2.get @meta_val.key /]", "value": "[aw2.get @meta_val.item.value /]", "compare": "[aw2.get @meta_val.item.compare default='=' /]", "type": "[aw2.get @meta_val.item.type default='CHAR' /]" } [/query.posts_builder] [/loop.@meta_val]
//***Posts Builder Part Taxo Query***// [loop.@taxo_val template.args.taxo not_empty='{template.args.taxo}'] [query.posts_builder part=tax_query not_empty="{@taxo_val.item.terms}"] { "taxonomy": "[aw2.get @taxo_val.key /]", "field": "[aw2.get @taxo_val.item.field default="ids" /]", "terms": [ [aw2.get @taxo_val.item.terms /] ], "include_children": [aw2.get @taxo_val.item.include_children default=true /] } [/query.posts_builder] [/loop.@taxo_val] [query.posts_builder part=run set="template.result" /] [template.return template.result /] [/templates.add]


