				$(".number-only").keydown(function (e) {
        // Allow: backspace, delete, tab, escape, enter and .
        if ($.inArray(e.keyCode, [46, 8, 9, 27, 13, 110]) !== -1 ||
             // Allow: Ctrl+A, Command+A
            (e.keyCode == 65 && ( e.ctrlKey === true || e.metaKey === true ) ) || 
             // Allow: home, end, left, right, down, up
            (e.keyCode >= 35 && e.keyCode <= 40)) {
                 // let it happen, don't do anything
                 return;
        }
        // Ensure that it is a number and stop the keypress
        if ((e.shiftKey || (e.keyCode < 48 || e.keyCode > 57)) && (e.keyCode < 96 || e.keyCode > 105))              {
            e.preventDefault();
        }
    });

  $('.alphabets-only').keypress(function (e) {

          // Allow: backspace, delete, tab, escape, enter and .
        if ($.inArray(e.keyCode, [46, 8, 9, 27, 13, 110]) !== -1 ||
             // Allow: Ctrl+A, Command+A
            (e.keyCode == 65 && ( e.ctrlKey === true || e.metaKey === true ) ) || 
             // Allow: home, end, left, right, down, up
            (e.keyCode >= 35 && e.keyCode <= 40)) {
                 // let it happen, don't do anything
                 return;
        }

        var regex = new RegExp("^[a-zA-Z ]+$");
        var str = String.fromCharCode(!e.charCode ? e.which : e.charCode);
        if (regex.test(str)) {
            return true;
        }
        else
        {
          e.preventDefault();
          return false;
        }
    });

  $("input.number-only,input.alphabets-only, .disable-paste").on("paste", function(){  
    // do your stuff here
      return false;
  });

  $(".movetop").on('click', function(){
        $('html,body').animate({scrollTop: 0}, 800);
    }); 


  document.addEventListener("mousewheel", function(event){
    if(document.activeElement.type === "number"){
        document.activeElement.blur();
    }
});

var validation_rule={};
validation_rule.order=10;
validation_rule.message='';
validation_rule.attribute='honeypot';
validation_rule.fn=function(o){

if ($('.has-error').length > 0) { 
  $('html, body').animate({scrollTop: $('.form-group.has-error:first').offset().top - $("#header").height()-10}, 1000);  
    $('.form-group.has-error:first .form-control').focus();
    return false;
  }else{
    return true;
  }
}
