var validation_rule={};
validation_rule.order=5;
validation_rule.message="<i class='fa fa-exclamation-triangle icon icon-warning'></i> Terms & Conditions are required!";
validation_rule.attribute="accept_checkbox";
validation_rule.fn=function(o){
  if (o.el.prop('checked'))
  { 	
  	return true;
  }		
	return false;
}
spa.validation_rules.push(validation_rule);  



var validation_rule={};
validation_rule.order=3;
validation_rule.message='Min amount ';
validation_rule.attribute='min';
validation_rule.fn=function(o){

var min_val=parseInt(o.el.attr("min"));
var current_val=parseInt(o.el.val());
if (current_val >= min_val) { 
    	return true;
  }else{
    return false;
  }
}
spa.validation_rules.push(validation_rule);    


