validation_rule.order=4;
validation_rule.message='Max amount ';
validation_rule.attribute='max';
validation_rule.fn=function(o){

var max_val=parseInt(o.el.attr("max"));
var current_val=parseInt(o.el.val());
if (current_val <= max_val) { 
    	return true;
  }else{
    return false;
  }
}
