spa.showmore_api={};
spa.showmore_api.run=function(o){
  var el=o.block.trigger;

  $(el).parents(".mainblock").find(".eclipscontent").toggleClass("eclips");

  if(el.text() == "+ Show More")
  {
  	$(el).text("- Show Less");
  }else{
  		$(el).text("+ Show More");
  }

  $(el).toggleClass("btn-primary btn-warning");
}



$('.collapsible h3').append("<i class='icon pull-right icon-circle-arrow-top' aria-hidden='true'></i>");
