     $(this).siblings("[role=content]").slideToggle().addClass("active-slideup"); 
     $(this).find('.icon').toggleClass("icon-circle-arrow-top icon-circle-arrow-bottom"); 
 })
 $('.collapsible.closed h3 .icon').toggleClass("icon-circle-arrow-top icon-circle-arrow-bottom");
 $('.collapsible.closed').siblings("[role=content]").slideToggle().addClass("active-slideup");
  $('.toggle_blocks').click(function(){
    if ( $(this).hasClass("open") ) {
       $(".collapsible").siblings("[role=content]").slideDown();
       $(".collapsible").find('.icon').addClass("icon-circle-arrow-top").removeClass("icon-circle-arrow-bottom"); 
       $(this).removeClass("open")
    }else{
       $(".collapsible").siblings("[role=content]").slideUp(); 
       $(".collapsible").find('.icon').addClass("icon-circle-arrow-bottom").removeClass("icon-circle-arrow-top "); 
       $(this).addClass("open")
    }

 })

 $('.toggle_blocks_parent').click(function(){
 var parentnode=$(this).parents("[role=parentblock]");
    if ( $(this).hasClass("open") ) {
      parentnode.find("[role=content]").slideDown();
        parentnode.find('.icon').addClass("icon-circle-arrow-top").removeClass("icon-circle-arrow-bottom"); 
       $(this).removeClass("open")
    }else{
       parentnode.find("[role=content]").slideUp(); 
        parentnode.find('.icon').addClass("icon-circle-arrow-bottom").removeClass("icon-circle-arrow-top "); 
       $(this).addClass("open")
    }

 })

 spa.toggletext={};

 spa.toggletext.new=function(o){
   e=o.block.trigger;
   if (e.hasClass("more"))
   {
     e.text("- Show Less").siblings(".complete").show();  
     e.siblings(".eclips").hide(); 
     e.removeClass("more").addClass("less");
   }else{
     e.text("+ Show More").siblings(".complete").hide(); 
     e.siblings(".eclips").show();
     e.addClass("more").removeClass("less");
   }
   console.log(e.text());
 }

