[templates.add editor_lib]
	<script type="spa/axn" axn=core.run_script>
	spa.course_editor={};
	spa.course_editor.prerequisites={};

	spa.course_editor.prerequisites.js_files='https://cdn.getawesomestudio.com/lib/select2/4.0.3/js/select2.full.js,https://cdn.getawesomestudio.com/lib/Sortable/js/Sortable.js,https://cdn.getawesomestudio.com/lib/jquery.imgareaselect/0.9.10/js/jquery.imgareaselect.min.js,https://cdn.getawesomestudio.com/lib/jquery-form-element-repeater/0.1.0/js/jquery.form-repeater.js,https://cdn.getawesomestudio.com/lib/bootstrap/3.3.6/js/bootstrap.min.js';

	spa.course_editor.main=function(){

		//setup  tabs
		spa.course_editor.generate_tabs();
/*
		//setup tiny mce
		tinymce.init({
			selector: 'textarea.tinymce',
			relative_urls : false,
			remove_script_host : false,
			plugins: [
			'charmap colorpicker compat3x directionality hr image lists media paste tabfocus textcolor fullscreen wordpress wpautoresize wpeditimage wpgallery wplink wpdialogs wptextpattern wpview wpemoji'
			],
			toolbar: 'undo redo | styleselect | bold italic underline | alignleft aligncenter alignright alignjustify | bullist numlist outdent indent | link unlink image media | forecolor backcolor emoticons'
		});
	*/

		//setup char count
		$( "body" ).on( "keyup", "[maxlength]", function() {
			var lg_desc_maxLength = $( this ).attr('maxlength');
			var length = $(this).val().length;
			var length = lg_desc_maxLength-length;
			$( this ).parent().find('.help-block').text(length + ' characters remaining');
		});

		//capture Publish, Update & Save Draft buttons
		$('#save-post, #publish').on('click', function(){
			spa.course_editor.save();
			spa.course_editor.save_li();
		});

		//capture image update event
		var _URL = window.URL || window.webkitURL;
		$( "body" ).on( "change", "[data-imgtype]", function(e) {
			var image,
			  file,
			  ele_id = $( this ).attr( "id" ),
			  imgType = $( this ).data( "imgtype" ),
			  ele_name = $( this ).data( "name" ),
			  error_flag = "on";
			if ((file = this.files[0])) {
				image = new Image();
				image.onload = function() {

					switch (imgType) {

						case 'icon':             		
							if((this.width >= 32 && this.height >= 32) && this.width/this.height == 1){
							  error_flag = "off";
							}
						break;

						case 'sq-img':             		
							if((this.width >= 320 && this.height >= 320) && this.width/this.height == 1){
							  error_flag = "off";
							}
						break;

						case 'v-img':             		
							if((this.width >= 480 && this.height >= 640) && this.width/this.height == 0.75){
							  error_flag = "off";
							}
						break;		

						case 'h-img':            		
							if((this.width >= 640 && this.height >= 480) && ((this.width/this.height).toFixed(2) == 1.33)){
							  error_flag = "off";
							}
						break;

						default:            	  
						  if(this.width >= 640){
							  error_flag = "off";
							}
						break;
					}

					if(error_flag == "off"){
					  spa.course_editor.hide_img_error(ele_id);
					  spa.course_editor.upload_img(ele_id, ele_name);

					}else{
					  spa.course_editor.show_img_error(ele_id);
					}
				};
				image.src = _URL.createObjectURL(file);
			}
		});

		//Delete attached image
		$('body').on('click','span.delete-img',function () {
		  $(this).parent().prev().find('.img-preview').empty();
		  $(this).parent().empty();
		  $('[data-ele_id='+$(this).data('field_id')+']').val('');
		  $('#'+$(this).data('field_id')).val('').prop('required', true);
		  $('#'+$(this).data('field_id')).prev().find('.mute-text').remove();
		});
	}

	var img_hotspot = {};

	//initialize the HotSpot Upload File type
	spa.course_editor.initialize_hotspot_upload=function(){
		$('input[type="file"]').addClass('hotspot');
	}

	//initialize the HotSpot Creator
	spa.course_editor.initialize_hotspot=function(){
		img_hotspot = $('.init-hotspot').imgAreaSelect({ 
		  maxWidth: 45,
		  maxHeight: 45,
		  handles: true,
		  aspectRatio: '1:1',
		  instance: true
		});

		//capture the Image Hotspot selection & Confirm
		$('#confirm-img-hotspot').on('click', function(){

			var selection = img_hotspot.getSelection();
			var img_pos = $('.init-hotspot').position();

			var hotspot = [];

			hotspot.push(((selection.x1 - img_pos.left) / $('.init-hotspot').outerWidth() * 100).toFixed(2));
			hotspot.push(((selection.y1 - img_pos.top) / $('.init-hotspot').outerHeight() * 100).toFixed(2));

			$('div.tab-pane.active').find('input[disabled]').val(hotspot.join());

			img_hotspot.cancelSelection();
		});
	}

	//initialize the Select2 box  
	spa.course_editor.initialize_select2=function(o){
		var el = o.el;
		post_type = el.attr("post_type");
		app = el.attr("app");
		var typeahead_url = "[php.site_url /]/"+app+"/ajax/get-typeahead-data/"+post_type;

		$(".select2").select2({
			ajax: {
				url: typeahead_url,
				delay: 250,
				data: function (params) {
				  return {
					q: params.term
				  };
				},
				processResults: function (data, params) {
				  // parse the results into the format expected by Select2
				  // since we are using custom formatting functions we do not need to
				  // alter the remote JSON data, except to indicate that infinite
				  // scrolling can be used
				  params.page = params.page || 1;
				  data = $.parseJSON(data.trim());

				  return {
					results: data.items
				  };
				},
				cache: true
			},
			escapeMarkup: function (markup) { return markup; },
			minimumInputLength: 1,
			templateResult: spa.course_editor.formatTypes,
			templateSelection: spa.course_editor.formatTypesSelection
		});

		if($( '#title' ).val() == ""){
			$(".select2").prop("disabled", true);
		}

		//enable Activity Type selection
		$( "body" ).on( "keyup", "#title", function() {
			if($( this ).val() != ""){
			  $(".select2").prop("disabled", false);
        $(".load-template-data").attr("disabled", false);
			}else{
			  $(".select2").prop("disabled", true);
        $(".load-template-data").attr("disabled", true);
			}
		});

	};

	//initialize sortable list
	spa.course_editor.initialize_sortable=function(){
		var editable = document.getElementById("editable");

		editableList = Sortable.create(editable, {
			filter: '.js-remove',
			onUpdate: function (evt){
				spa.course_editor.save_li()
			},
			onSort: function (evt){
				spa.course_editor.save_li()
			},
			onFilter: function (evt) {
				var el = editableList.closest(evt.item); // get dragged item
				el && el.parentNode.removeChild(el);
			}
		});
	}

	spa.course_editor.formatTypes=function(types){
		if (types.loading) return types.text;

		var markup = "<div class='select2-result-type clearfix'>" +
		  "<div class='select2-result-type__meta'>" +
			"<div class='select2-result-type__title'>"+ types.id +" - "+ types.post_title + "</div>";

		if (types.post_content) {
			markup += "<div class='select2-result-type__description'>" + types.post_content + "</div>";
		}

		markup += "</div></div>";

		return markup;
	}

	spa.course_editor.formatTypesSelection=function(types){

  	if(types.post_title){
    	return types.id+" - "+ types.post_title;
    }else if(types.post_name){
    	 return types.id + " - " + types.post_name;
    }

	}

	spa.course_editor.show_img_error=function(ele_id){

		$("#"+ele_id).parent().addClass('has-error');
		$("#"+ele_id).parent().find($('span.help-block span.show')).removeClass('show').addClass('hide');

		//create a error msg span
		$('<span/>', {
			class: 'error',
			html: 'Incorrect Image selected.'
		}).appendTo($("#"+ele_id).parent().find($('span.help-block')));

	}

	spa.course_editor.hide_img_error=function(ele_id){

		$("#"+ele_id).parent().removeClass('has-error');
		$("#"+ele_id).parent().find($('span.help-block span.error')).remove();
		$("#"+ele_id).parent().find($('span.help-block span.hide')).removeClass('hide').addClass('show');

	}

	spa.course_editor.upload_img=function(ele_id, ele_name){
		spa.loader.show();
		var post_id = $('#post_ID').val();
		//var url = ajaxurl+ "?action=app_ajax&ajax=true&app=course-editor&slug=upload-image&ele_id="+ele_id+"&post_id="+post_id+"&name="+ele_name;
		var url = spa.settings.path+"ajax/upload-image?ele_id="+ele_id+"&post_id="+post_id+"&name="+ele_name;
		var file = document.querySelector("#"+ele_id);
		var fd = new FormData();

		fd.append(ele_id, file.files[0]);

		$.ajax({
			url: url,
			type: "POST",
			data: fd,
			processData: false,  // tell jQuery not to process the data
			contentType: false,   // tell jQuery not to set contentType
			success: function (res) {

				res = $.parseJSON(res.trim());
				$('input[name="'+ele_name+'"]').val(res.url);
				$('<img/>', {
					  src: res.url
					}).appendTo($("#"+ele_id).parent().find($('div.img-preview')));
					spa.loader.hide();
					$('<span/>', {
				  class:"btn btn-sm btn-danger delete-img",
					  html: 'Delete Image',
					  'data-field_id': ele_id
					}).appendTo($("#"+ele_id).parent().find($('div.img-actions')));

				if($('#'+ele_id).hasClass('hotspot')){
					$('div.img-preview').find('img').addClass('init-hotspot');
					$('<button/>', {
					  id: 'confirm-img-hotspot',
					  class: 'btn btn-secondary btn-sm',
					  type: 'button',
					  html: 'Confirm Selection'
					}).appendTo($("#"+ele_id).parent().find($('div.img-actions')));

					spa.course_editor.initialize_hotspot();

				}
			}
		});	  
	}

	spa.course_editor.generate_sortable_li=function(o){

		var el=o.el;
		res = $.parseJSON(el.text());
    var edit_url = o.block.edit_url+res.id;

		//create li
		$('<li/>', {
			class:"",
			'data-id': res.id,
			'data-title': res.title,
			html: res.id+" - "+res.title+'<a class="js-edit" href="'+edit_url+'" target="_blank" rel="noopener noreferrer"><i class="material-icons">create</i></a><i class="js-remove material-icons">clear</i>',
		}).appendTo('#editable');
	}

	//save function loops through form elements.
	spa.course_editor.save=function(){
  /*
		// Collection data for tinymce
		$('form#post').find('.tinymce').each(function(){
			tinyMCE.get($(this).attr('id')).save();
		})

    */
		var elements = [];
		$('form#post').find('[data-activity="yes"]').each(function( index ) {

			var obj = {};

			if(($( this ).val() != "") && ($( this ).val() != null)){
			  obj.name = $( this ).attr('name');
		    obj.value = $( this ).val();

			  elements.push(obj);
			}
		});

		var metadata = JSON.stringify(elements);
    console.log(metadata);
		$("textarea.metadata").val(metadata);
	}

	//save function loops through sortable LI elements.
	spa.course_editor.save_li=function(){
  /*
      // Collection data for tinymce
      $('form#post').find('.tinymce').each(function(){
        tinyMCE.get($(this).attr('id')).save();
      })
    */
		var elements = [];
		$('form#post').find('#editable li').each(function( index ) {
			var obj = {};
			obj.id = $( this ).data('id');
			obj.title = $( this ).data('title');
			elements.push(obj);
		});

		if (elements.length > 0) {
		  var metadata = JSON.stringify(elements);
		  $("textarea.metadata").val(metadata);
		}

	}

	spa.course_editor.auto_save=function(app,slug,meta_key){	  
		post_id = $('#post_ID').val();

		if(post_id != ''){
			if(slug == "autosave-activity-post")
			  spa.course_editor.save();
			else
			  spa.course_editor.save_li();

			//send the ajax reqest to save the data.
			var activity_data = $("textarea.metadata").val();
			var activity_type = $("#quiz-question-type").val();
			var search_data = $("#title").val();

			var url = "[aw2.get function.site_url /]/"+app+"/ajax/"+slug+"?post_ID="+post_id;

			fd = new FormData();
			fd.append(meta_key, activity_data);

			if(typeof activity_type != 'undefined' && activity_type != '' )
				fd.append('activity_type', activity_type);

      if(typeof search_data != 'undefined' && search_data != '' ){
        var keywords = $( "#Keywords option:selected" ).text();
        if(keywords){
          search_data = search_data+' '+$('#Keywords').val().join(" ");
        }
        fd.append('search_data', search_data);
      }

			$.ajax({
				url: url,
				type: "POST",
				data: fd,
				processData: false,  // tell jQuery not to process the data
				contentType: false   // tell jQuery not to set contentType
			});
		}
	}

	spa.course_editor.generate_tabs=function(){
	console.log('generate_tabs');
		//create a tab navigation ul
		$('<ul/>', {
			class:"nav nav-tabs",
			role:"tablist"
		}).appendTo('.tab-elements');

		//create a tab-content div
		$('<div/>', {
			class: 'tab-content'
		}).appendTo('.tab-elements');

		$("li[role='presentation']").each(function(){

			$rnd = spa.course_editor.randomString(5);
			var dummy = $(this).clone();
			dummy.find('a[role="tab"]').remove();
			$x=$(this).nextUntil('li');

			//$(this).contents(':not("a")').remove();
			$(this).find('a').attr("href", '#'+$rnd);

			$(this).appendTo("ul.nav-tabs");

			//create a tab-pane div
			$('<div/>', {
			  id: $rnd,
			  class: 'tab-pane'
			}).append($x).appendTo('.tab-content');
		});

		$('.nav-tabs a:first').tab('show');

	}

	spa.course_editor.randomString=function(len, charSet) {
  	charSet = charSet || 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
  	var randomString = '';
  	for (var i = 0; i < len; i++) {
  		var randomPoz = Math.floor(Math.random() * charSet.length);
  		randomString += charSet.substring(randomPoz,randomPoz+1);
  	}
  	return randomString;
  }

</script>
