  spa.addNotification = function(o){
      var hold = o.hold || 2000;
      var bgcolor = o.bgcolor || 'rgb(50, 50, 50)';
      var icon = o.icon || 'message';
      if($("body").children(".notifications").length){
          $('<div class="notification br-bottom" style="background: '+bgcolor+'"><div class="item-content"><div class="item-inner justify-content-start"><span class="material-icons pad-r-4">'+icon+'</span><div class="item-title">'+o.message+'</div></div></div></div>')
         .prependTo('.notifications')
         .hide()
         .slideDown()
         .delay(hold)
         .slideUp(function() {
            $(this).remove();
         });
