   spa.showModal = function(o){
      var title = o.title || '';
      var text = o.text || '';
      var afterText = o.afterText || '';
      var verticalButtons = o.verticalButtons || false;
      var buttons = o.buttons || '';
      $("#popup-modal .modal-title").html(title);
      $("#popup-modal .modal-text").html(text);
      $("#popup-modal .modal-aftertext").html(afterText);
      if(verticalButtons){
          $("#popup-modal .modal-buttons").addClass("modal-buttons-vertical");
      }
      else{
      		$("#popup-modal .modal-buttons").removeClass("modal-buttons-vertical");
      }
      $("#popup-modal .modal-buttons").html('');
      $.each( o.buttons, function( i, val ) {
          $("#popup-modal .modal-buttons").append('<span class="modal-button" data-dismiss="modal">'+val["text"]+'</span>');
      });
