[multi.select]
CREATE TABLE IF NOT EXISTS `plot` ( `ID` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT, `stamp` datetime NOT NULL DEFAULT current_timestamp(), `updated_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL, `object_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL, `coll_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL, `coll_type` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL, `meta_key` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL, `meta_value` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL, PRIMARY KEY (`ID`), KEY `meta_key` (`meta_key`), KEY `coll_id` (`coll_id`), KEY `object_id` (`object_id`), KEY `meta_value` (`meta_value`(100)), KEY `combo` (`object_id`,`coll_id`,`meta_key`), KEY `coll_type` (`coll_type`), KEY `combo2` (`coll_id`,`meta_key`,`meta_value`(100)), KEY `coll_id_meta_key` (`coll_id`,`meta_key`) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `plot_activities` ( `ID` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT, `stamp` datetime NOT NULL DEFAULT current_timestamp(), `updated_by` varchar(100) NOT NULL, `object_id` varchar(50) NOT NULL, `coll_id` varchar(50) NOT NULL, `coll_type` varchar(50) NOT NULL, `meta_key` varchar(50) NOT NULL, `meta_value` longtext NOT NULL, PRIMARY KEY (`ID`) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
ALTER TABLE `plot_activities` ADD INDEX `object_id` (`object_id`), ADD INDEX `coll_id` (`coll_id`), ADD INDEX `coll_id_meta_key_meta_value` (`coll_id`, `meta_key`, `meta_value`(100));
COMMIT;
[/multi.select]

