	<script type=spa/axn axn='core.run_script' bind='click' bind_selector='.js_copy_code' >
		document.querySelector("textarea").select();
		document.execCommand('copy');
	</script>

	<script type=spa/axn axn='core.run_script' bind='click' bind_selector='.js_paste_code' >
		let pasteArea = document.getElementById('data-content');
        pasteArea.value = '';

        navigator.clipboard.readText()
        .then((text)=>{
            pasteArea.value = text;
        });
	</script>

    //** trigger paste on page load**//
    <script type=spa/axn axn='core.run_script'>
	    $(".js_paste_code").trigger( "click" );
    </script>
