[templates.add get_data]
    [template.set data-content={request2.data-content}/]
    [php.str_replace p1="echo" p2="dump" p3="{template.data-content}" o.set=template.data-content/]
    [date.create now date_format='Y-m-d H:i:s' o.set=template.dt /]
    <div axn=selector.update selector='#raw_block' get='me.html'>
	  	<template id='contentblock' data-check_id='[app.safe_id/]'>
    		<h5>Result On: [template.dt/]</h5>
    		[template.data-content.run/]
    	</template>
	</div>

    //****Rendering data in iframe****//
    <script type="spa/axn" axn=core.run_script data-safe_id='[app.safe_id/]'>
        var htmlblock=$('#contentblock').html();
        var doc = document.getElementById('contentpreview').contentWindow.document;
        doc.open();
        doc.write(htmlblock);
        doc.close();			
    </script>
[/templates.add]
