			[css.less]
.for(@list, @code) {
&{
  .loop(@i:1) when (@i =< length(@list)) {
	  @value: extract(@list, @i);
	  @code();
	  .loop(@i + 1);
  }
  .loop();
}
}

// margin Classes
@mar: @pad-1,@pad-2,@pad-3,@pad-4,@pad-5,@pad-6,@pad-7,@pad-8,@pad-9,@pad-10;
.for(@mar, {
.mar-t-@{i} {margin-top: @value;}
.mar-b-@{i}{ margin-bottom: @value; }
.mar-x-@{i}{ margin-left: @value; margin-right: @value; }
});
