[templates.add main]
  [loop.@i module.handlers]
    [module.get config.{@i.key}.label set="module.temp_label" /]
    [module.set temp_token={token}  /]
    <button type="button" class="btn btn-primary js-submit-job form-btns-[aw2.get @i.item.label default='{module.temp_label}' /]" role="form_handler" id="[aw2.get @i.key /]" spa_unique="[module.temp_token /]">[aw2.get @i.item.label default="{module.temp_label}" /]</button>

    <script type="spa/axn" axn="core.run_script">
      $("[spa_unique='[module.temp_token /]']").click(function(){
        var article=$(this).parents('[role=search]');
        var search_id=article.attr('id');
        var form=$(this).parents('[role=search]').find('[role=form] form');

        var action=$(this).attr('id');
        form.attr('submit_handler',action);
        form.attr('pageno',1);
        form.attr('search_id',search_id);
        form.attr('new_query','yes');
        form.submit();
      });
    </script>
  [/loop.@i]

  [if.not_empty module.auto_handler]
    <script type="spa/axn" axn="core.run_script">
    	var article=$('[role=search]');
    	var search_id=article.attr('id');
    	var form=$('[role=search]').find('[role=form] form');

    	var action='[module.auto_handler /]';
    	form.attr('submit_handler',action);
    	form.attr('pageno',1);
    	form.attr('search_id',search_id);
    	form.attr('new_query','yes');
    	form.submit();
    </script>
  [/if.not_empty]

