item.content:encoded:
//*****generate ticket****//
[templates.add generate]

  	//******default mod: single-submit when, template.mod is empty******//
    [template.set mod="single-submit" c.empty="template.mod"/]

    //******default service_type: single_service when, template.service_type is empty******//
    [template.set service_type="single_service" c.empty="template.service_type"/]

	//****generate ticket****//
	[session_ticket.create nonce=yes set=template.ticket/]     
	[template.set_array collection post_type={template.service_type} /]

	//*****set activity against the ticket*****//
	[session_ticket.set_activity {template.ticket} collection={template.collection} module='{template.mod}.{template.tpl}' /]
	[session_ticket.set {template.ticket} field="block_details" value={single_data.block.json_encode}/]

	//*****return ticket*****//
	[template.return template.ticket/]

[/templates.add]

//******display fields with label and value*****//
[templates.add display]

	//******render fields in horizontal manner*****//
	[templates.horizontal_display/]

    //*****get meta_value against the meta_key from lapp_meta*****//
	[env.get single_data.block.meta_data.{@field.key} o.set=template.meta_value/]

	//*****render label and value******//
    [form_control.label label="{@field.item.label}:" value={template.meta_value} args={module.args}/]

[/templates.add]

//******render edit fields******//
[templates.add edit]

	//******render fields in horizontal manner*****//
	[templates.horizontal_display/]

    //*****get meta_value against the meta_key from lapp_meta*****//
	[env.get single_data.block.meta_data.{@field.key} o.set=template.meta_value/]

    //*****render label and editable value, when field edit is yes******//
    [form_control.run {@field.item.data_type} id={@field.key} 
    	label="{@field.item.label}:" 
        value={template.meta_value} 
        taxonomy={@field.item.taxonomy}
        array_options={@field.item.arr_options}
        user-role={@field.item.user-role}
        posttype={@field.item.post_type}
        args={module.args}
        required={@field.item.required}
        c.cond="@field.item.edit"
        c.eq="yes"/]

    //*****render label and value, when field edit is no******//
    [form_control.label label="{@field.item.label}:" value={template.meta_value} args={module.args} c.cond="@field.item.edit" 		c.eq="no"/]

[/templates.add]

//*****add button section****//
[templates.add add_btn]

	//*****add button section****//
	 <div class="form-group">
       <div class="col-md-12 text-center">

         //***when form is add form***//
         <button type="submit" name="singlebutton" class="btn btn-primary js-submit-job btn-sm">
              <i class="fa fa-check"></i>Add
         </button>

         <div class="gap-3"></div>
      </div>
    </div>

    //****script to udpate*****//
	<script type=spa/axn axn='form.ajax' bind='submit' bind_selector='#[template.form_name/]' lapp_id="[env.single_data.lapp_id/]" route_ajax='t/[template.ticket/]' disable_selector='.js-submit-job'></script>

[/templates.add]

//******show edit button******//
[templates.add edit_btn]

	//*****update button section****//
	 <div class="form-group">
       <div class="col-md-12 text-center">

         //***when form is edit form***//
         <button type="submit" name="singlebutton" class="btn btn-primary js-submit-job btn-sm">
              <i class="fa fa-check"></i>Update
         </button>

         <div class="gap-3"></div>
      </div>
    </div>

    //****script to udpate*****//
	<script type=spa/axn axn='form.ajax' bind='submit' bind_selector='#[template.form_name/]' route_ajax='t/[template.ticket/]' disable_selector='.js-submit-job'></script>

[/templates.add]

//*****update button section****//
[templates.add update_btn]

	//*****update button section****//
	 <div class="form-group">
       <div class="col-md-12 text-center">

         //***when form is edit form***//
         <button type="submit" name="singlebutton" class="btn btn-primary js-submit-job btn-sm">
              <i class="fa fa-check"></i>Update
         </button>

         //*****form close button*******//
         <button type="button" name="closeblock" class="btn btn-danger js-closeblock btn-sm">
          <i class="fa fa-times "></i> Close
         </button>
         <div class="gap-3"></div>
      </div>
    </div>

    //****script to udpate*****//
	<script type=spa/axn axn='form.ajax' bind='submit' bind_selector='#[template.form_name/]' route_ajax='t/[template.ticket/]' disable_selector='.js-submit-job'></script>

	//*****script to close*****//
    <script type=spa/axn axn='core.run_script' bind='click' bind_selector='.js-closeblock' >
      o.block.trigger.parents('[role=edit]').empty();
      //***$('html, body').animate({
          scrollTop: $("#[module.get key_type/]").offset().top
      }, 1000);****//
    </script>

[/templates.add]

//******horizontal field display*****//
[templates.add horizontal_display]

    //******display form label and values in horizontal manner*****//
  	[module.set_array args form_type="horizontal" bs3_label_class="col-md-4 text-right" bs3_control_class="col-md-8"/]

[/templates.add]

//***single page wrapper***//
[templates.add main]

  //******display form label and values in horizontal manner*****//
  [module.set_array args form_type="horizontal" bs3_label_class="col-md-4 text-right" bs3_control_class="col-md-8"/]

  //****loop through all the blocks****//
  [loop.@block single_data.blocks set=template.html] 
      [env.get @block.item o.set=single_data.block /]
      [templates.render_block /]
  [/loop.@block]

  //*******render page******//
  [ui.outer-block title={template.header} html={template.html}/]

[/templates.add]

//*****render single block*****//
[templates.add render_block]

	//****store action in variable at template level, default is: display_form*****//
    [env.get single_data.block.action set=template.action default="display_form"/]

    //*****get unique_id for each block*****//
    [template.set unique_id = "section_{unique_number}"/]

    //****set empty html***//
    [template.set html=''/]



    //*****when action is display_form******//
    [if.empty template.html]
    	[templates.display_form set=template.html c.cond="template.action" c.eq="s:display_form"/]
    [/if.empty]

    //*******render section block section*****//
    [form_control.section id={template.unique_id} html={template.html} title={single_data.block.label} open={single_data.block.open}/]

[/templates.add]

//****when action is add_form****//
[templates.add add_form]

    //*****if fields are not empty, loop through all the fields******//
    [loop.@field single_data.block.field set=template.html c.not_empty="single_data.block.field"]
        [this_collection.field.edit/]
    [/loop.@field]

    //******generating form name******//
    [template.set form_name="add-form-{unique_number}"/]

    //****section field html*****//
    <section role="display">

        <form id="[template.form_name/]">

          //*****render fields******//
          [template.html/]

          //*****if edit is not empty, show edit button****//
          [content.run c.not_empty="single_data.block.add_service.action"]

             //*****generate tickets****//
             [this_collection.ticket.generate tpl='add_meta' set=template.ticket/]

             //****generating add_btn for current form****//
             [this_collection.field.add_btn ticket={template.ticket} form_name={template.form_name}/]

          [/content.run]

        </form>

    </section>

[/templates.add]

//****when action is edit_form****//
[templates.add edit_form]

	//*****if fields are not empty, loop through all the fields******//
    [loop.@field single_data.block.field set=template.html c.not_empty="single_data.block.field"]
        [this_collection.field.edit/]
    [/loop.@field]

    //****section field html*****//
    <section role="display">

        //******generating form name******//
        [template.set form_name="edit-form-{unique_number}"/]

        <form id="[template.form_name/]">

    	  //***render form fields***//
          [template.html/]

          //*****if edit is not empty, show edit button****//
          [content.run c.not_empty="single_data.block.edit_service.action"]

             //*****generate tickets****//
             [this_collection.ticket.generate tpl='update_meta' set=template.ticket/]

             //****generating edit_btn for current form****//
             [this_collection.field.edit_btn ticket={template.ticket} form_name={template.form_name}/]

          [/content.run]

      	</form>

    </section>

[/templates.add]

//****when action is display_form****//
[templates.add display_form]


    //*****if fields are not empty, loop through all the fields******//
    [loop.@field single_data.block.field set=template.html c.not_empty="single_data.block.field"]
        [this_collection.field.display/]
    [/loop.@field]

    //****section field html*****//
    <section role="display">
        [template.html/]
    </section>

    //*****if edit is not empty, show edit button****//
    [content.run c.not_empty="single_data.block.edit_service.action"]

       //*****generate tickets****//
       [this_collection.ticket.generate tpl='edit_block' set=template.ticket/]

        //****generate edit button****//
        <div class="text-right">
            <button bind="click" axn="control.update" control="parents.group-details.sections.edit" 
                route_ajax="t/[template.ticket/]" class="btn btn-xs btn-success btn-sixth" >
                    <i class="fa fa-pencil-square-o"></i> EDIT 
            </button>
        </div>

    [/content.run]

    <div class="gap-3"></div>

    //*****edit area*****//
    <section role="edit" style="display:flex;"></section>

[/templates.add]

//***loads custom service***//
[templates.add custom_loader]

   //****set passed collection in a variable****//
   [template.set coll = {single_data.block.custom_service.action.dot}/]

   //**run module**//
 	[module.run {template.coll.1} template={template.coll.2} post_type={template.coll.0}/]

[/templates.add]
